    public boolean check()
    {
         Float f=0f;
         boolean check=false;
        try{
            f=Float.valueOf(jTextFieldDelayTime.getText());
        if(f<Float.valueOf(jLabelMinimumDelay.getText()))Popup.show("It cannot be smaller than minimum value");
        else if(f>Float.valueOf(jLabelMaximumDelay.getText()))Popup.show("It cannot be larger than maximum value");
        else    check=true;
        }
        catch(Exception e){
            Popup.show("Invalid Number");
            jTextFieldDelayTime.setText("1");
        }
         return check;

    }

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        if(check()){
        int choice=jComboBoxDelayChoice.getSelectedIndex();
        if(choice==0)delayReg1();
        else if(choice==1)delayReg2();
        else if(choice==2)delayReg3();
        else if(choice==3)delaySubRegPair();
        dispose();
        }

    }//GEN-LAST:event_jButton1ActionPerformed

