    public void choiceHide(int choice)
    {
        int tState=0;
        try{
            tState=Integer.parseInt(jTextFieldTstate.getText());
        }catch(Exception e){jTextFieldTstate.setText(String.valueOf(tState));}
        jComboBoxReg1.setVisible(false);
        jComboBoxReg2.setVisible(false);
        jComboBoxReg3.setVisible(false);
        jComboBoxRegPair.setVisible(false);
        switch(choice){
            case 0:jComboBoxReg1.setVisible(true);
                   jComboBoxReg1.setSelectedIndex(1);
                   jLabelMinimumDelay.setText(String.valueOf((0.028*tState)/1000));
                   jLabelMaximumDelay.setText(String.valueOf((3.584*tState)/1000));
                    break;
            case 1:jComboBoxReg1.setVisible(true);
                   jComboBoxReg2.setVisible(true);
                   jComboBoxReg1.setSelectedIndex(1);
                   jComboBoxReg2.setSelectedIndex(2);
                   jLabelMinimumDelay.setText(String.valueOf((0.046*tState)/1000));
                   jLabelMaximumDelay.setText(String.valueOf((914.954*tState)/1000));

                   break;
            case 2:jComboBoxReg1.setVisible(true);
                   jComboBoxReg2.setVisible(true);
                   jComboBoxReg3.setVisible(true);
                   jComboBoxReg1.setSelectedIndex(1);
                   jComboBoxReg2.setSelectedIndex(2);
                   jComboBoxReg3.setSelectedIndex(3);
                   jLabelMinimumDelay.setText(String.valueOf((0.064*tState)/1000));
                   jLabelMaximumDelay.setText(String.valueOf((233.314304*tState)));
                   break;
            case 3:jComboBoxRegPair.setVisible(true);
                   jLabelMinimumDelay.setText(String.valueOf((0.408*tState)/1000));
                   jLabelMaximumDelay.setText(String.valueOf((1.572857*tState)));
                   break;
            default:break;
        }
    }

    public String delaySubRegPair()
    {
        String s="";
        int tState=0;
        try{
            tState=Integer.parseInt(jTextFieldTstate.getText());
        }catch(Exception e){jTextFieldTstate.setText(String.valueOf(tState));}

        int TStates=0;
        try{
            TStates=(int) (Double.parseDouble(jTextFieldDelayTime.getText())*1000*1000);
        }catch(Exception e){jTextFieldTstate.setText(String.valueOf(TStates));}

        int count=((TStates/tState) - 17)/24;
        String space="\t   ";
        String D=jComboBoxRegPair.getItemAt(jComboBoxRegPair.getSelectedIndex()).toString();
        String E=String.valueOf(D.charAt(2));
        D=String.valueOf(D.charAt(0));
        s=jTextFieldDelayLabel.getText()+":";
        s=s+"\n"+space+"LXI "+D+","+o.engine.Dec2Hex(count);
        s=s+"\n"+jTextFieldDelayLabel.getText()+"Loop:\n"+space+"DCX "+D;
        s=s+"\n"+space+"MOV A,"+D;
        s=s+"\n"+space+"ORA "+E;
        s=s+"\n"+space+"JNZ "+jTextFieldDelayLabel.getText()+"Loop";
        s=s+"\n"+space+"RET";
        o.jTextAreaAssemblyLanguageEditor.setText(o.jTextAreaAssemblyLanguageEditor.getText()+s);
        o.textEditor.colorEditor();
        return s;
    }

    public String delayReg1()
    {
        String s="";
        int tState=0;
        try{
            tState=Integer.parseInt(jTextFieldTstate.getText());
        }catch(Exception e){jTextFieldTstate.setText(String.valueOf(tState));}

        int TStates=0;
        try{
            TStates=(int) (Double.parseDouble(jTextFieldDelayTime.getText())*1000*1000);
        }catch(Exception e){jTextFieldTstate.setText(String.valueOf(TStates));}

        int noTstate=(TStates/tState);
        int count=(noTstate/14)-1;
        String space="\t   ";
        String B=jComboBoxReg1.getItemAt(jComboBoxReg1.getSelectedIndex()).toString();
        s=jTextFieldDelayLabel.getText()+":";
        s=s+"\n"+space+"MVI "+B+","+o.engine.Dec2Hex2digit(count);
        s=s+"\n"+jTextFieldDelayLabel.getText()+"Loop:\n"+space+"DCR "+B;
        s=s+"\n"+space+"JNZ "+jTextFieldDelayLabel.getText()+"Loop";
        s=s+"\n"+space+"RET";
        o.jTextAreaAssemblyLanguageEditor.setText(o.jTextAreaAssemblyLanguageEditor.getText()+s);
        o.textEditor.colorEditor();
        return s;
    }

    public int[] solveReg2(int tstates)
    {
        int present=0,x=0,y=0,a[]={0,0},past=0,px=0,py=0;
        for(x=0;x<256;x++)
        {
            for(y=0;y<256;y++)
            {
                present=14+18*x+14*x*y;  
                if(present/tstates>=1&&present/tstates<1.00002){
                    if(past==0)past=present;
                    if(past%tstates>=present%tstates)
                    {
                    past=present;px=x;py=y;
                    }
                }
            }
        }
         a[0]=px;a[1]=py;
        return a;
    }

    public String delayReg2()
    {
        String s="";
        int tState=0;
        try{
            tState=Integer.parseInt(jTextFieldTstate.getText());
        }catch(Exception e){jTextFieldTstate.setText(String.valueOf(tState));}

        int TStates=0;
        try{
            TStates=(int) (Double.parseDouble(jTextFieldDelayTime.getText())*1000*1000);
        }catch(Exception e){jTextFieldTstate.setText(String.valueOf(TStates));}

        int noTstate=(TStates/tState);
        int count[]=solveReg2(noTstate);
        String space="\t   ";
        String B=jComboBoxReg1.getItemAt(jComboBoxReg1.getSelectedIndex()).toString();
        String C=jComboBoxReg2.getItemAt(jComboBoxReg2.getSelectedIndex()).toString();
        s=jTextFieldDelayLabel.getText()+":";
        s=s+"\n"+space+"MVI "+B+","+o.engine.Dec2Hex2digit(count[0]);
        s=s+"\n"+jTextFieldDelayLabel.getText()+"Loop1:\n"+space+"MVI "+C+","+o.engine.Dec2Hex2digit(count[1]);
        s=s+"\n"+jTextFieldDelayLabel.getText()+"Loop2:\n"+space+"DCR "+C;
        s=s+"\n"+space+"JNZ "+jTextFieldDelayLabel.getText()+"Loop2";
        s=s+"\n"+space+"DCR "+B;
        s=s+"\n"+space+"JNZ "+jTextFieldDelayLabel.getText()+"Loop1";
        s=s+"\n"+space+"RET";
        o.jTextAreaAssemblyLanguageEditor.setText(o.jTextAreaAssemblyLanguageEditor.getText()+s);
        o.textEditor.colorEditor();
        return s;
    }

    public int[] solveReg3(long tstates)
    {
        long present=0,past=0;
        int x=0,y=0,z=0,a[]={0,0,0},px=0,py=0,pz=0;
        for(x=0;x<256;x++)
        {
            for(y=0;y<256;y++)
            {
               for(z=0;z<256;z++)
                {
                 present=14+18*x+18*x*y+14*x*y*z;
                 if(present/tstates>=1&&present/tstates<1.00002){
                     if(past==0)past=present;
                    if(past%tstates>=present%tstates)
                    {
                        past=present;px=x;py=y;pz=z;
                    }
                 }
                }
            }
        }
         a[0]=px;a[1]=py;a[2]=pz;
        return a;
    }

    public String delayReg3()
    {
        String s="";
        long tState=0;
        try{
            tState=Integer.parseInt(jTextFieldTstate.getText());
        }catch(Exception e){jTextFieldTstate.setText(String.valueOf(tState));}

        long TStates=0;
        try{
            TStates=(long) (Double.parseDouble(jTextFieldDelayTime.getText())*1000*1000);
        }catch(Exception e){jTextFieldTstate.setText(String.valueOf(TStates));}

        long noTstate=(TStates/tState);
        int count[]=solveReg3(noTstate);
        String space="\t   ";
        String B=jComboBoxReg1.getItemAt(jComboBoxReg1.getSelectedIndex()).toString();
        String C=jComboBoxReg2.getItemAt(jComboBoxReg2.getSelectedIndex()).toString();
        String D=jComboBoxReg3.getItemAt(jComboBoxReg3.getSelectedIndex()).toString();
        s=jTextFieldDelayLabel.getText()+":";
        s=s+"\n"+space+"MVI "+B+","+o.engine.Dec2Hex2digit(count[0]);
        s=s+"\n"+jTextFieldDelayLabel.getText()+"Loop1:\n"+space+"MVI "+C+","+o.engine.Dec2Hex2digit(count[1]);
        s=s+"\n"+jTextFieldDelayLabel.getText()+"Loop2:\n"+space+"MVI "+D+","+o.engine.Dec2Hex2digit(count[2]);
        s=s+"\n"+jTextFieldDelayLabel.getText()+"Loop3:\n"+space+"DCR "+D;
        s=s+"\n"+space+"JNZ "+jTextFieldDelayLabel.getText()+"Loop3";
        s=s+"\n"+space+"DCR "+C;
        s=s+"\n"+space+"JNZ "+jTextFieldDelayLabel.getText()+"Loop2";
        s=s+"\n"+space+"DCR "+B;
        s=s+"\n"+space+"JNZ "+jTextFieldDelayLabel.getText()+"Loop1";
        s=s+"\n"+space+"RET";
        o.jTextAreaAssemblyLanguageEditor.setText(o.jTextAreaAssemblyLanguageEditor.getText()+s);
        o.textEditor.colorEditor();
        return s;
    }


